using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Net.Http;
using Newtonsoft.Json;
using Newtonsoft.Json.Linq;
using Xamarin.Forms;

namespace ProyectoMovil
{
    public partial class ProductsPage : ContentPage
    {
        List<Modelos.Producto> lista = new List<Modelos.Producto>();
        public ProductsPage()
        {
            InitializeComponent();
            ObtenerListaProductos();
        }

        private async void ObtenerListaProductos()
        {
            using (HttpClient cliente = new HttpClient())
            {
                var respuesta = await cliente.GetAsync("https://appmovil2.herokuapp.com/Api/Productos");

                if (respuesta.IsSuccessStatusCode)
                {
                    string contenido = respuesta.Content.ReadAsStringAsync().Result.ToString();
                    //JObject jsonObj = JObject.Parse(contenido.ToString());
                    dynamic dyn = JsonConvert.DeserializeObject(contenido);

                    //List<ListaProductos> products = JsonConvert.DeserializeObject<List<ListaProductos>>(dyn.productos.ToString());

                    foreach (var item in dyn.productos)
                    {
                        string img64 = item.imagen.ToString();
                        byte[] newBytes = Convert.FromBase64String(img64);
                        var stream = new MemoryStream(newBytes);

                        lista.Add(new Modelos.Producto(
                                        item.pk.ToString(), item.nombre_producto.ToString(),
                                        item.descripcion.ToString(), item.fecha_expira.ToString(),
                                        item.precio.ToString(), item.cantidad.ToString(),
                                        item.tipo_producto.ToString(), item.fecha_registro.ToString(),

                                        ImageSource.FromStream(() => new MemoryStream(newBytes)),
                                        img64
                                        ));

                    }


                    //var pathBase64 = "";
                    //byte[] newBytes = Convert.FromBase64String(pathBase64);
                    //ImagenPath.Source = ImageSource.FromStream(() => new MemoryStream(newBytes));
                    lsProductos.ItemsSource = null;

                    lsProductos.ItemsSource = lista;

                }
            }

        }



        private async void lsProductos_ItemSelected(object sender, SelectedItemChangedEventArgs e)
        {   //Evento para obtener los datos del producto
            var productoSeleccionado = (Modelos.Producto)e.SelectedItem;

            
            object producto = new
            {
                id = productoSeleccionado.IdProducto,
                nombre = productoSeleccionado.nombre_producto,
                descripcion = productoSeleccionado.descripcion,
                precio = productoSeleccionado.precio,
                cantidad = productoSeleccionado.cantidad,
                imagen = ImageSource.FromStream(() => new MemoryStream(Convert.FromBase64String(productoSeleccionado.img64)))

            };

            var detalle = new DetalleProducto();
            detalle.BindingContext = producto;
            await Navigation.PushAsync(detalle);
            //await DisplayAlert("Notificación", productoSeleccionado.nombre_producto, "Comprar", "Cancelar");
        }

        private void SearchBar_TextChanged(object sender, TextChangedEventArgs e)
        {
            if (string.IsNullOrEmpty(e.NewTextValue))
            {
                lsProductos.ItemsSource = lista;
            }
            else
            {
                lsProductos.ItemsSource = lista.Where(x => x.nombre_producto.Contains(e.NewTextValue));
            }
        }
    }
    
}
